mapper.select:
	select rnum, class_notice_no, class_nm_s,  class_cate_no, class_notice_title, mid, class_notice_date
	from (select rownum as rnum, new_t.class_notice_no,new_t.class_nm_s, new_t.class_cate_no,new_t.class_notice_title, new_t.mid, new_t.class_notice_date
        	from (select rownum as rnum, n.class_notice_no, class.CLASS_NM_S, class.CLASS_CATE_NO,n.class_notice_title, m.mid, n.class_notice_date 
                from class_notice n, class, member m
                where class.class_no = n.class_no and m.mid = class.tutor_id
                order  by n.class_notice_no desc) new_t
        where rownum <= #{endRowNo}
        )   
		where rnum >=#{startRowNo}
